function selectALlCodes(){var codeSelects=document.querySelectorAll('.click_to_select_all');Array.prototype.forEach.call(codeSelects,function(codeSelect){codeSelect.addEventListener('click',function(){this.select()})})}
function findDropdowns(){var dropObjects=document.querySelectorAll('.dropdown');Array.prototype.forEach.call(dropObjects,function(dropObject){dropObject.addEventListener('click',function(e){e.preventDefault(this);e.stopPropagation();toggleDropdown(this)},false);})}
function toggleDropdown(e){var thisDropDown=e.querySelector('.dropdown-content');thisDropDown.style.display=(thisDropDown.style.display!='block'?'block':'');thisDropDown.addEventListener('click',function(e){e.stopPropagation(this);});var dropDownMenuItems=e.querySelectorAll('.dropdown-content > a');Array.prototype.forEach.call(dropDownMenuItems,function(dropDownItem){dropDownItem.addEventListener('click',function(e){this.parentElement.style.display='';})})
document.addEventListener('click',function(){closeAllDropdowns();})}
